Bash(powershell -NoProfile -File - <<'PS1'
Get-ChildItem "HKCU:\\Software\\Microsoft\\Windows\\CurrentVersion\\Uninstall" | ForEach-Object {
  $props = Get-ItemProperty $_.PSPath -ErrorAction SilentlyContinue
  if \($props.DisplayName -like '*WQ*' -or $props.DisplayName -like '*wq*'\) {
    Write-Output "Name: $\($props.DisplayName\)"
    Write-Output "Location: $\($props.InstallLocation\)"
  }
}
PS1)

Bash(powershell -NoProfile -File - <<'PS1'
$currentPath = [Environment]::GetEnvironmentVariable\("Path", "User"\)
$installDir = "E:\\Program Files\\WQBot"
if \($currentPath -notlike "*$installDir*"\) {
    $newPath = "$currentPath;$installDir"
    [Environment]::SetEnvironmentVariable\("Path", $newPath, "User"\)
    Write-Output "PATH 已更新，添加了: $installDir"
} else {
    Write-Output "PATH 中已包含: $installDir"
}
PS1)
